function picData() {  
  
    // plot.init();  
    // setPreference();  
    // setSector(1,1,1,1);  
    plot.scale(0.8, 0.8);  
      
    var _3d = new Shape3D();  
      
    plot.translate(0, 180);  
      
    drawPath2(_3d.pollar, 0, 0, 100);  
      
    plot.translate(0, -60);  
    drawPath2(_3d.brick, 0, 0, 100);  
      
    plot.translate(0, -60);  
    drawPath2(_3d.cubic, 0, 0, 50);  
      
      
    for (var i = 0; i < 3; i++) {  
        plot.translate(0, -40);  
        drawPath2(_3d.brick, 0, 0, 60);  
    }  
    for (var i = 0; i < 3; i++) {  
        plot.translate(0, -35);  
        drawPath2(_3d.brick, 0, 0, 30);  
    }  
      
    plot.translate(0, -40);  
    drawPath2(_3d.sphere, 0, 0, 40);  
}  

function picData2() {  
    // plot.init();  
    // setPreference();  
    // setSector(1,1,1,1);  
    plot.translate(150,100);  
        plot.scale(1.5,1.5);  
      
    var _3d = new Shape3D();  
      
    //左上角开始画，两个球，一个正方体，一个长方体  
    plot.save();  
    plot.translate(-200, -150);  
    drawPath2(_3d.sphere, 0,0, 30);  
    plot.translate(20,20);  
    drawPath2(_3d.sphere, 0,0, 30);  
    plot.translate(150,-20);  
    drawPath2(_3d.cubic, 100, 0, 60);  
    plot.restore();  
      
    plot.save()  
        plot.translate(-150, -120)  
        plot.scale(1, 0.4);       
    drawPath2(_3d.brick, 0, 0, 180);      
    plot.restore();  
  
    //两个圆柱  
    plot.save()  
        plot.translate(-150, -80)  
        plot.scale(1.5, 1);  
    drawPath2(_3d.pollar, 0,0, 60);  
    plot.restore();  
      
    plot.save()  
        plot.translate(0, -80)  
        plot.scale(1.5, 1);  
    drawPath2(_3d.pollar, 0,0, 60);  
    plot.restore();  
      
    //三块板  
    plot.save()  
        plot.translate(-100, -50)  
        plot.scale(1, 0.2);       
    drawPath2(_3d.brick, 0, 0, 300);      
    plot.restore();  
      
    plot.save()  
        plot.translate(-140, -20)  
        plot.scale(1, 0.6);       
    drawPath2(_3d.brick, 0, 0, 100);      
    plot.restore();  
      
    plot.save()  
        plot.translate(-180, 0)  
        plot.scale(1, 0.6);       
    drawPath2(_3d.brick, 0, 0, 100);      
    plot.restore();  
      
    //右下角两个圆柱  
    plot.save()  
        plot.translate(-20, -15);  
    drawPath2(_3d.pollar, 0,0, 60);  
    plot.translate(20, 5);  
    drawPath2(_3d.pollar, 0,0, 60);  
    plot.restore();  
  
}